import React from "react";
import styled from "styled-components";
import CartItem from "../Cart/CartItem";
import CartButton from "../Cart/CartButton";
import { useSelector } from "react-redux";

const Cart = () => {
  const state = useSelector((state) => state);
  const storeItems = [
    {
      name: "Barska GB12166 Fitness Watch with Heart Rate Monitor",
      price: "$49.99",
      body_location: "Wrist",
      category: "Fitness",
      _id: 6543,
      imageSrc:
        "data:image/jpeg;base64,",
      numInStock: 9,
      companyId: 19962,
    },
    {
      name: "Belkin GS5 Sport Fit Armband, Black F8M918B1C00",
      price: "$24.99",
      body_location: "Arms",
      category: "Fitness",
      _id: 6544,
      imageSrc:
        "data:image/jpeg;base64,",
      numInStock: 9,
      companyId: 16384,
    },
    {
      name: "Bowflex EZ Pro Strapless Heart Rate Monitor Watch, Black",
      price: "$12.99",
      body_location: "Wrist",
      category: "Medical",
      _id: 6545,
      imageSrc:
        "data:image/jpeg;base64,",
      numInStock: 5,
      companyId: 11385,
    },
  ];

  const calculateTotalItems = (arr) => {
    let totalItems = 0;

    arr.forEach((item) => {
      totalItems += item.quantity;
    });
    return totalItems;
  };

  const calculateTotalPrice = (arr) => {
    let totalPrice = 0;

    arr.forEach((item) => {
      totalPrice += item.quantity * item.price;
    });
    return <span>${totalPrice / 100}</span>;
  };

  return (
    <Wrapper>
      <BagWrapper>
        <div
          style={{
            border: "1px solid red",
            margin: "20px",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-around",
            width: "100%",
          }}
        >
          <h1>My cart</h1>

          <StyledP>{calculateTotalItems(storeItems)} items</StyledP>
        </div>

        <div>
          <ul>
            {storeItems.map((item) => {
              return (
                <li key={item._id} style={{ listStyleType: "none" }}>
                  <CartItem item={item} />
                </li>
              );
            })}
          </ul>
        </div>
      </BagWrapper>

      <BuyWrapper>
        <StyledP>Total: {calculateTotalPrice(storeItems)}</StyledP>
        <CartButton style={{ width: "120px", height: "40px" }}>
          Purchase
        </CartButton>
      </BuyWrapper>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 80%;
  height: 300px;
  color: #000;
  padding-top: 20px;
`;

const BagWrapper = styled.div``;

const BuyWrapper = styled.div`
  display: flex;
  justify-content: space-around;
  bottom: 40px;
  width: 360px;
`;

const StyledP = styled.p`
  color: #b0a2b0;
`;

export default Cart;
